mapper.select:
select * from diary
         order by diary_no desc
         limit #{start}, #{amount}

select good_check
from good
where diary_no = #{diaryNo} and user_no = #{userNo}

mapper.update:
update good
set  good_check = "true"
where diary_no = #{diaryNo} and user_no = #{userNo}

update good
set  good_check = "false"
where diary_no = #{diaryNo} and user_no = #{userNo}

update diary
set diary_good = diary_good + 1
where diary_no = #{diaryNo}

update diary
set diary_good = diary_good - 1
where diary_no = #{diaryNo}
